import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { Row } from "react-bootstrap";
import { useState,useEffect } from "react";
import Modal from "react-bootstrap/Modal";
import Image from "react-bootstrap/Image";
const genres = require('./data/genres.json')
console.log(genres)

const AddGenres = ({ movie, addGoalHandler }) => {
//   const { title, year, thumbnail, cast, genres } = movie;
  const [active, setActive] = useState(false);
  const [loadedGoals, setLoadedGoals] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  async function addGoalHandler(goalText) {
    // setIsLoading(true);

    try {
      const response = await fetch('http://localhost/genre', {
        method: 'POST',
        body: JSON.stringify({
          genres: genres,
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const resData = await response.json();
      // console.log(resData.movies)
      if (!response.ok) {
        throw new Error(resData.message || 'Adding the goal failed.');
      }
      // console.log(resData)
      // setLoadedGoals(resData.movies.results.map((a)=>  
      // a.title));

    } catch (err) {
      console.log(err)
      // setError(
      //   err.message ||
      //     'Adding a goal failed - the server responsed with an error.'
      // );
    }
    // setIsLoading(false);
  }

  useEffect(function () {
    async function fetchData() {
      setIsLoading(true);

      try {
        const response = await fetch('http://localhost/genre');

        const resData = await response.json();

        if (!response.ok) {
          throw new Error(resData.message || 'Fetching the goals failed.');
        }
        console.log(resData.goals)
        setLoadedGoals(resData.goals);
      } catch (err) {
        setError(
          err.message ||
            'Fetching goals failed - the server responsed with an error.'
        );
      }
      setIsLoading(false);
    }

    fetchData();
  }, []);


  function handleModal() {
    setActive(!active);
  }

  return active ? (
    <MyVerticallyCenteredModal
      show={active}
      onHide={handleModal}
    //   movie={movie}
    />
  ) :! loadedGoals.length?(
    <div>
        Nothing happened
    </div>
  ): (
    <Card style={{ width: "18rem", margin: "10px" }}>
      <Card.Img
        variant="top"
        src={""}
        alt={'ds'}
        width={259}
        height={380}
      />
      <Card.Body>
        <Card.Title>{loadedGoals[0].name}</Card.Title>

      </Card.Body>
      <Card.Footer>
        <Row>
          <Button
            className="lml-auto"
            variant="primary"
            type="submit"
            value="Send"
            onClick={() => addGoalHandler("title")}
          >
            Post
          </Button>
          <Button
            className="ml-auto"
            variant="primary"
            type="submit"
            value="Send"
            onClick={handleModal}
          >
            More
          </Button>
        </Row>
      </Card.Footer>
    </Card>
  );
};

export default Favorites;
