import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';


const Movie = ({ movie, addGoalHandler }) => {
    const { title, year, thumbnail, cast, } = movie;

    return (
      <Card style={{ width: '18rem', margin:'10px' }}>
        <Card.Img variant="top" src={thumbnail} alt={title} width={259} height={380}/>
        <Card.Body>
          <Card.Title>{title}</Card.Title>
          <Card.Text>
            {title} was released on {year}
            {cast}
          </Card.Text>
          <Button variant="primary" type='submit' value="Send" onClick={()=>addGoalHandler(title)}>Post</Button>
          <Button variant="primary" type='submit' value="Send" onClick={()=>addGoalHandler(title)}>More</Button>
        </Card.Body>
      </Card>
    );

  };

  export default Movie;
