import { Switch, Route } from "react-router-dom";
import { Container, Col, Row,Button } from "react-bootstrap";
import Account from "./Account";
import FreeComponent from "./FreeComponent";
import AuthComponent from "./AuthComponent";
import ProtectedRoutes from "./ProtectedRoutes";
import MovieContainer from "./components/MovieContainer";

import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Cookies from "universal-cookie";
import Login from "./Login";
import { useState,useEffect } from "react";
import BeforeLogin from "./BeforeLogin";
import Register from "./Register";
const cookies = new Cookies();


const logout = () => {
  // destroy the cookie

  cookies.remove("TOKEN", { path: "/" });
  // redirect user to the landing page
  window.location.href = "/";
}
const confirmAction = () => {
  const response = window.confirm("Are you sure you want to do that?");

  if (response) {
      logout();
      alert("Ok was pressed");
  } else {
      alert("Cancel was pressed");
  }
}

function App() {
const [username,setUserName] = useState('');

  return (

    <Container style={{background:'red'}} >

      <Navbar bg="light" expand="md">

      <Container style={{background:'blue'}} >
        <Navbar.Brand href="#">Navbar scroll</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link href="/">Home</Nav.Link>
            <Nav.Link href="/register">Register</Nav.Link>
          </Nav>


          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success">Search</Button>
          </Form>          


          <NavDropdown style={{ justifyContent:"end" }} title={
                <img
                  src="https://mdbcdn.b-cdn.net/img/Photos/Avatars/img(31).webp"
                  className="rounded-circle"
                  height="22"
                  alt="Avatar"
                  loading="lazy"
                />} id="navbarScrollingDropdown">
              <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
              <NavDropdown.Item href="/auth">
                Go to Movies
              </NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action5" onClick={()=>logout()}>
                logout
              </NavDropdown.Item>

            </NavDropdown>

          {/* <Button type="submit" variant="danger" onClick={() => confirmAction()}>
            Logout
          </Button>  */}

        </Navbar.Collapse>
      </Container>
    </Navbar>



      {/* create routes here */}
      <Switch>
        <Route exact path="/" component={BeforeLogin} />
        <Route exact path="/login" component={Login} />
        <Route exact path="/register" component={Register} />
        <Route exact path="/free" component={FreeComponent} />
        <ProtectedRoutes path="/auth" component={MovieContainer} />
      </Switch>
    </Container>

  );
}

export default App;
