import { useEffect, useState } from 'react';
import { Button } from "react-bootstrap";
import Movie from './Movie';
import './movie.css';
import Cookies from "universal-cookie";
  const cookies = new Cookies();
const data = require('../data/movies-2010s.json').slice(0,20).filter((movie)=>{
  if(movie.thumbnail && movie.cast.length >0 && movie.thumbnail_height>380){
    return movie
  }});;


const MovieContainer = ({addGoalHandler}) => {

  const logout = () => {
    // destroy the cookie
    cookies.remove("TOKEN", { path: "/" });
    // redirect user to the landing page
    window.location.href = "/";
  }

  return(
    <div className="movie-container">
      <h2>Movies</h2>
      <ul className ="movie-list">
        {data.map(movie => (
          <Movie movie={movie} key={movie.title} addGoalHandler={addGoalHandler}/>
        ))}
      </ul> 
      {/* <Button type="submit" variant="danger" onClick={() => logout()}>
        Logout
      </Button>      */}
    </div>
  );
};

export default MovieContainer;
