import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { Row } from "react-bootstrap";
import { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Image from "react-bootstrap/Image";

function MyVerticallyCenteredModal(props) {
  const { title, year, thumbnail, cast, genres } = props.movie;
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">{title}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <img
          src={thumbnail}
          style={{ display: "block", marginLeft: "auto", marginRight: "auto" }}
        />
        <h4>Centered Modal</h4>
        <p>
          {title} was released on {year}, {genres}
          {cast}
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

const Movie = ({ movie, addGoalHandler }) => {
  const { title, year, thumbnail, cast, genres } = movie;
  const [active, setActive] = useState(false);

  function handleModal() {
    setActive(!active);
  }

  return active ? (
    <MyVerticallyCenteredModal
      show={active}
      onHide={handleModal}
      movie={movie}
    />
  ) : (
    <Card style={{ width: "18rem", margin: "10px" }}>
      <Card.Img
        variant="top"
        src={thumbnail}
        alt={title}
        width={259}
        height={380}
      />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text>
          {title} was released on {year}, {genres}
          {cast}
        </Card.Text>
        {/* <Button style={{justifyContent:'left'}}variant="primary" type='submit' value="Send" onClick={()=>addGoalHandler(title)}>Post</Button>
          <Button className='ml-auto'style={{justifyContent:'right'}}variant="primary" type='submit' value="Send" onClick={()=>addGoalHandler(title)}>More</Button> */}
      </Card.Body>
      <Card.Footer>
        <Row>
          {active && (
            <Button className="btn btn-success btn-sm ml-auto mr-auto">
              Like
            </Button>
          )}
          <Button
            classname="ml-auto"
            variant="primary"
            type="submit"
            value="Send"
            onClick={() => addGoalHandler(title)}
          >
            Post
          </Button>
          <Button
            className="ml-auto"
            variant="primary"
            type="submit"
            value="Send"
            onClick={handleModal}
          >
            More
          </Button>
        </Row>
      </Card.Footer>
    </Card>
  );
};

export default Movie;
