import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { Row } from 'react-bootstrap';
import { useState } from 'react';
const Movie = ({ movie, addGoalHandler }) => {
    const { title, year, thumbnail, cast,genres } = movie;
    const [active,setActive]= useState(false)

    function handleModal(){
      setActive(!active)
    }
    return (
      <Card style={{ width: '18rem', margin:'10px' }}>
        <Card.Img variant="top" src={thumbnail} alt={title} width={259} height={380}/>
        <Card.Body>
          <Card.Title>{title}</Card.Title>
          <Card.Text>
            {title} was released on {year}, {genres}
            {cast}
          </Card.Text>
          {/* <Button style={{justifyContent:'left'}}variant="primary" type='submit' value="Send" onClick={()=>addGoalHandler(title)}>Post</Button>
          <Button className='ml-auto'style={{justifyContent:'right'}}variant="primary" type='submit' value="Send" onClick={()=>addGoalHandler(title)}>More</Button> */}
        </Card.Body>
        <Card.Footer>
          <Row>
            {active && <Button className="btn btn-success btn-sm ml-auto mr-auto">Like</Button>}
            <Button classname="ml-auto"variant="primary" type='submit' value="Send" onClick={()=>addGoalHandler(title)}>Post</Button>
            <Button className='ml-auto'variant="primary" type='submit' value="Send" onClick={handleModal}>More</Button>
          </Row>
        </Card.Footer>
      </Card>
    );

  };

  export default Movie;
